export const getLogs = () => {
  return dispatch => {
    dispatch({type: "LOADING_LOGS"});
    return fetch('/logs')
      .then(resp => resp.json())
      .then(logs => dispatch({type: "LOADED_LOGS", payload: logs}))
  }
}

//return function (dispatch)
  //dispatch action to start the loading proccess
  // return fetch url
    //get response
    //dispatch data


export const addLog = (log) => {
  return dispatch => {
    dispatch({type: "ADD_LOG"})
    return fetch('/logs', {
      method: "POST",
      headers: {'Content-Type':'application/json'},
      body: JSON.stringify({
        "learned": this.log.learned,
        "unsure": this.log.unsure,
        "goals_accomplished": this.log.goals_accomplished,
        "goals_tomorrow": this.log.goals_tomorrow,
        "date": this.log.date,
        "mood": this.log.mood,
        "title": this.log.title
      })
    })
  }
}



